import axios from 'axios';

  import memeDetail from '../components/MemeDetail/MemeDetail.vue'

  export default {
    components: {
      memeDetail
    },
    data() {
      return {
        memes: [],
        currentPage: 1,
        perPage: 10,
        totalPages: 0,
        fullMemesList: [],
      }
    },
    mounted() {
      this.fetchMemes();
    },
    methods: {
      fetchMemes() {
        axios.get(`https://api.imgflip.com/get_memes`)
          .then(response => {
            this.fullMemesList = response.data.data.memes;
            this.getMemeListToShow();
            // this.totalPages = response.data.total_pages;
          })
          .catch(error => {
            console.log(error);
          });
      },
      prevPage() {
        this.currentPage--;
        this.fetchMemes();
      },
      nextPage() {
        this.currentPage++;
        this.fetchMemes();
      },
