import axios from 'axios';

  import memeDetail from '../components/MemeDetail/MemeDetail.vue'

  export default {
    components: {
      memeDetail
    },
    data() {
      return {
        memes: [],
        currentPage: 1,
        perPage: 10,
        totalPages: 0,
        fullMemesList: [],
      }
    },
    mounted() {
      this.fetchMemes();
    },
    methods: {
      fetchMemes() {
        axios.get(`https://api.imgflip.com/get_memes`)
          .then(response => {
            this.fullMemesList = response.data.data.memes;
            this.getMemeListToShow();
            // this.totalPages = response.data.total_pages;
          })
          .catch(error => {
            console.log(error);
          });
      },
      prevPage() {
        this.currentPage--;
        this.fetchMemes();
      },
      nextPage() {
        this.currentPage++;
        this.fetchMemes();
      },
      serachMemeToShow(event) {
        const textToSearch = event.target.value || ''
        if (textToSearch && textToSearch.trim().length > 0) {
            this.memes = this.fullMemesList.filter(obj => this.transformText(obj.name).includes(this.transformText(textToSearch)));
            // const res = arr.filter(obj => Object.values(obj).some(val => val.includes(search)));
        }
      },
      transformText(text) {
        return text.toUpperCase().replaceAll(' ', '')
      }

    }
  }
